import React, { useState, useEffect, useRef } from "react";

const Chatbot = () => {
    const [userInput, setUserInput] = useState("");
    const [responses, setResponses] = useState([]);
    const chatboxRef = useRef();

    useEffect(() => {
        // Welcoming message
        const welcomeMessage = "ðŸ‘‹ Welcome to the Health Chatbot! How can I assist you today?";
        setResponses([welcomeMessage]);
        
        // Auto-focus on the input field when the component mounts
        const inputField = document.getElementById("user-input");
        if (inputField) {
            inputField.focus();
        }
    }, []);

    const handleUserInput = (e) => {
        setUserInput(e.target.value);
    };

    const handleSend = async () => {
        if (userInput.trim()) {
            // Simulate sending the user's input to the backend and getting a response
            const newResponse = await simulateApiCall(userInput); // Replace with your API call
            setResponses((prevResponses) => [...prevResponses, `You said: ${userInput}`, newResponse]);
            setUserInput("");
        }
    };

    const simulateApiCall = async (input) => {
        // Simulate an API call delay
        return new Promise((resolve) => {
            setTimeout(() => {
                // Simulated response based on user input
                const responses = {
                    "hello": "Hello! How can I help you?",
                    "symptoms": "Please tell me your symptoms one by one.",
                    "bye": "Goodbye! Take care!",
                };
                resolve(responses[input.toLowerCase()] || "I'm not sure how to respond to that.");
            }, 1000);
        });
    };

    const handleClearChat = () => {
        setResponses([]);
        const welcomeMessage = "ðŸ‘‹ Welcome to the Health Chatbot! How can I assist you today?";
        setResponses([welcomeMessage]);
    };

    useEffect(() => {
        if (chatboxRef.current) {
            chatboxRef.current.scrollTop = chatboxRef.current.scrollHeight;
        }
    }, [responses]);

    return (
        <div className="chatbot-container p-6 bg-white rounded-lg shadow-md">
            <h2 className="text-2xl font-semibold mb-4">ðŸ¤– Chatbot</h2>
            <div className="chatbox border border-gray-300 p-4 h-64 overflow-y-auto" ref={chatboxRef}>
                {responses.map((response, index) => (
                    <p key={index}>{response}</p>
                ))}
            </div>
            <div className="flex mt-4">
                <input
                    id="user-input"
                    type="text"
                    value={userInput}
                    onChange={handleUserInput}
                    placeholder="Type your message..."
                    className="border border-gray-300 rounded-l-lg p-2 flex-1"
                />
                <button
                    onClick={handleSend}
                    className="bg-blue-600 text-white rounded-r-lg p-2"
                >
                    Send
                </button>
            </div>
            <button onClick={handleClearChat} className="bg-red-600 text-white rounded p-2 mt-2">
                Clear Chat
            </button>
        </div>
    );
};

export default Chatbot;
