import React, { useState, useEffect } from "react";
import twilio from "twilio";

// Twilio configuration
const client = new twilio('ACa58e5d71b82a166aa33bb9f1c37e9d22', 'd5449ef48c110c6c104ac59ae276d2fe');
const twilioServiceSid = 'VAb528bad60ed0802b60e72fc7100efb34';

// Helper function to select a Twilio number
const selectTwilioNumber = () => {
  const twilioNumbers = ['+919632983944', '+918804339456'];
  return twilioNumbers[Math.floor(Math.random() * twilioNumbers.length)];
};

// Lab Tests Component
const LabTests = () => {
  const [selectedTest, setSelectedTest] = useState(null);
  const [isPaymentSuccess, setIsPaymentSuccess] = useState(false);
  const [isPaymentProcessing, setIsPaymentProcessing] = useState(false);
  const [successMessage, setSuccessMessage] = useState("");

  useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://checkout.razorpay.com/v1/checkout.js";
    script.async = true;
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  const sendConfirmationMessage = (phoneNumber, testName) => {
    const twilioNumber = selectTwilioNumber();

    client.messages
      .create({
        body: `Payment successful for your lab test: ${testName}. Thank you for using our service!`,
        from: twilioNumber,
        to: phoneNumber,
      })
      .then((message) => {
        console.log("Message sent:", message.sid);
      })
      .catch((error) => {
        console.error("Error sending message:", error);
      });
  };

  const handleSelectTest = (test) => {
    setSelectedTest(test);
    setIsPaymentSuccess(false);
    setSuccessMessage("");
  };

  const handlePaymentSuccess = (test) => {
    setIsPaymentSuccess(true);
    setIsPaymentProcessing(false);
    setSuccessMessage("Payment successful! You can now view the test guidelines.");

    // Send confirmation message
    const userPhoneNumber = "+91XXXXXXXXXX"; // Replace with the user's phone number dynamically
    sendConfirmationMessage(userPhoneNumber, test.name);
  };

  const handlePaymentFailure = () => {
    setIsPaymentProcessing(false);
    alert("Payment failed or was canceled. Please try again.");
  };

  const initiatePayment = (test) => {
    if (window.Razorpay) {
      setIsPaymentProcessing(true);
      const options = {
        key: "rzp_test_lmkOFuIPmT2vi9",
        amount: parseFloat(test.price.replace("â‚¹", "")) * 100,
        currency: "INR",
        name: "Health App",
        description: "Lab Test Payment",
        handler: (response) => {
          if (response.razorpay_payment_id) {
            handlePaymentSuccess(test);
          }
        },
        prefill: {
          name: "Test User",
          email: "testuser@example.com",
          contact: "+91XXXXXXXXXX", // Replace dynamically
        },
        notes: {
          address: "Health App Office",
        },
        theme: {
          color: "#3399cc",
        },
        modal: {
          ondismiss: handlePaymentFailure,
        },
      };

      const razorpayInstance = new window.Razorpay(options);
      razorpayInstance.open();
    } else {
      console.error("Razorpay SDK failed to load.");
      handlePaymentFailure();
    }
  };

  return (
    <div className="p-6 bg-light-gray rounded-lg shadow-2xl">
      <h2 className="text-3xl font-semibold text-soft-blue mb-4">Available Lab Tests</h2>
      <ul className="space-y-4">
        {labTests.map((test, index) => (
          <li
            key={index}
            className={`border-b pb-4 ${
              selectedTest === test ? "bg-blue-50 border-blue-500" : ""
            }`}
          >
            <div className="flex justify-between items-center">
              <div>
                <h3 className="text-xl font-bold text-gray-800">{test.name}</h3>
                <p className="text-gray-600">Price: {test.price}</p>
              </div>
              <button
                className="bg-soft-blue text-white px-4 py-2 rounded-lg shadow-2xl transition duration-300 ease-in-out hover:scale-105 animate-button-pulse"
                onClick={() => handleSelectTest(test)}
              >
                View Guidelines
              </button>
            </div>
            {selectedTest === test && !isPaymentSuccess && (
              <div className="mt-2 p-4 border-t border-gray-300">
                <h4 className="text-lg font-semibold text-gray-700">Please make a payment to view guidelines:</h4>
                <button
                  className={`mt-2 bg-green-500 text-white px-4 py-2 rounded-lg shadow-2xl ${
                    isPaymentProcessing ? "opacity-50 cursor-not-allowed" : ""
                  }`}
                  onClick={() => initiatePayment(test)}
                  disabled={isPaymentProcessing}
                >
                  {isPaymentProcessing ? "Processing..." : "Pay Now"}
                </button>
              </div>
            )}
            {selectedTest === test && isPaymentSuccess && (
              <div className="mt-2 p-4 border-t border-gray-300">
                <h4 className="text-lg font-semibold text-gray-700">Guidelines:</h4>
                <p className="text-gray-600">{test.guidelines}</p>
              </div>
            )}
          </li>
        ))}
      </ul>
      {successMessage && (
        <div className="mt-6 p-4 bg-green-100 text-green-700 rounded-lg shadow-md">
          <p>{successMessage}</p>
        </div>
      )}
    </div>
  );
};

export default LabTests;
