// LabTest.js
import React, { useState } from 'react';

const labTests = [
    { name: "Complete Blood Count (CBC)", price: "$30", description: "A comprehensive blood test for overall health." },
    { name: "Liver Function Test (LFT)", price: "$40", description: "Assesses the health of your liver." },
    { name: "Thyroid Profile", price: "$25", description: "Tests for thyroid function abnormalities." },
    { name: "Blood Sugar Test", price: "$20", description: "Measures glucose levels for diabetes screening." },
    { name: "Lipid Profile", price: "$35", description: "Cholesterol and lipid levels to assess heart health." },
];

const LabTest = () => {
    const [selectedTest, setSelectedTest] = useState(null);

    return (
        <div className="container mx-auto mt-10 p-5 bg-gradient-to-br from-blue-50 to-blue-100 rounded-lg shadow-lg">
            <h1 className="text-4xl font-extrabold text-center text-blue-600 mb-8">Lab Test Information</h1>
            <p className="text-center text-gray-700 mb-6">Select a test to view details and pricing information.</p>

            <div className="grid gap-6 sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-3">
                {labTests.map((test, index) => (
                    <div
                        key={index}
                        className={`p-6 bg-white rounded-lg shadow-md hover:shadow-xl transition-shadow duration-300 ${
                            selectedTest === index ? 'ring-2 ring-blue-500' : ''
                        }`}
                        onClick={() => setSelectedTest(selectedTest === index ? null : index)}
                    >
                        <div className="text-lg font-semibold text-blue-600">{test.name}</div>
                        <div className="text-gray-500 mt-1">{test.price}</div>
                        {selectedTest === index && (
                            <div className="mt-4 text-gray-600">
                                <p>{test.description}</p>
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default LabTest;
