import React, { useState } from "react";

const LabTest = () => {
    const [selectedTest, setSelectedTest] = useState(null);

    return (
        <div>
            <h1>Available Lab Tests</h1>
            <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
                {labTests.map((test, index) => (
                    <div
                        key={index}
                        style={{
                            border: "1px solid #ccc",
                            borderRadius: "10px",
                            padding: "20px",
                            width: "300px",
                            cursor: "pointer",
                            background: selectedTest === index ? "#f0f8ff" : "#fff",
                        }}
                        onClick={() => setSelectedTest(index)}
                    >
                        <h2>{test.name}</h2>
                        <p><strong>Price:</strong> {test.price}</p>
                    </div>
                ))}
            </div>

            {selectedTest !== null && (
                <div style={{ marginTop: "20px" }}>
                    <h2>Guidelines for {labTests[selectedTest].name}</h2>
                    <p>{labTests[selectedTest].guidelines}</p>
                </div>
            )}
        </div>
    );
};

export default LabTest;

const labTests = [
    { name: "General Physician (Dr. Arjun Sharma)", price: "₹740", guidelines: "Avoid eating heavy meals at least 2 hours before the appointment." },
    // (Include the rest of the labTests array here)
];
