import React, { useState } from 'react';

const labTests = [
    { name: "General Physician (Dr. Arjun Sharma)", price: 740, guidelines: "Avoid eating heavy meals at least 2 hours before the appointment." },
    { name: "Neurologist (Dr. Suresh Rao)", price: 10660, guidelines: "Ensure a full night's sleep, avoid stimulants, and list down any observed symptoms." },
    // Add more entries as needed
];

function LabTest() {
    const [selectedTest, setSelectedTest] = useState(null);
    const [paidTests, setPaidTests] = useState([]);

    const loadRazorpayScript = () => {
        return new Promise((resolve) => {
            const script = document.createElement("script");
            script.src = "https://checkout.razorpay.com/v1/checkout.js";
            script.onload = () => resolve(true);
            script.onerror = () => resolve(false);
            document.body.appendChild(script);
        });
    };

    const handlePayment = async (test) => {
        const res = await loadRazorpayScript();

        if (!res) {
            alert("Razorpay SDK failed to load. Are you online?");
            return;
        }

        const options = {
            key: "your_razorpay_key_here", // Replace with your Razorpay API key
            amount: test.price * 100, // Convert to smallest currency unit (paise)
            currency: "INR",
            name: "Lab Test Payment",
            description: `Payment for ${test.name}`,
            image: "https://your-logo-url.com/logo.png", // Optional company logo
            handler: function (response) {
                alert(`Payment successful for ${test.name}. Payment ID: ${response.razorpay_payment_id}`);
                setPaidTests((prevPaidTests) => [...prevPaidTests, test.name]);
            },
            prefill: {
                name: "Your Name", // Replace with dynamic user data
                email: "your-email@example.com", // Replace with dynamic user data
                contact: "9999999999", // Replace with dynamic user data
            },
            theme: {
                color: "#3399cc",
            },
        };

        const paymentObject = new window.Razorpay(options);
        paymentObject.open();
    };

    const displayGuidelines = (test) => {
        if (paidTests.includes(test.name)) {
            setSelectedTest(test);
        } else {
            alert("Please make a payment to view the guidelines.");
        }
    };

    return (
        <div style={{ padding: '20px', fontFamily: 'Arial' }}>
            <h1>Lab Tests</h1>
            <ul style={{ listStyleType: 'none', padding: 0 }}>
                {labTests.map((test, index) => (
                    <li
                        key={index}
                        style={{
                            border: '1px solid #ccc',
                            padding: '10px',
                            marginBottom: '10px',
                            borderRadius: '5px',
                        }}
                    >
                        <h3>{test.name}</h3>
                        <p>Price: â‚¹{test.price}</p>
                        {paidTests.includes(test.name) ? (
                            <button
                                style={{
                                    padding: '8px 12px',
                                    backgroundColor: '#4CAF50',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '5px',
                                    cursor: 'pointer',
                                }}
                                onClick={() => displayGuidelines(test)}
                            >
                                View Guidelines
                            </button>
                        ) : (
                            <button
                                style={{
                                    padding: '8px 12px',
                                    backgroundColor: '#f44336',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '5px',
                                    cursor: 'pointer',
                                }}
                                onClick={() => handlePayment(test)}
                            >
                                Make Payment
                            </button>
                        )}
                    </li>
                ))}
            </ul>
            {selectedTest && (
                <div
                    style={{
                        marginTop: '20px',
                        padding: '10px',
                        border: '1px solid #ccc',
                        borderRadius: '5px',
                        backgroundColor: '#f9f9f9',
                    }}
                >
                    <h2>Guidelines for {selectedTest.name}</h2>
                    <p>{selectedTest.guidelines}</p>
                </div>
            )}
        </div>
    );
}

export default LabTest;
