// App.js
import React, { useState } from 'react';
import './App.css';
import { registerUser, sendOtp, verifyOtp } from './api';

function App() {
    const [registerData, setRegisterData] = useState({
        name: '',
        age: '',
        gender: '',
        mobileNumber: ''
    });
    const [mobileNumber, setMobileNumber] = useState('');
    const [otp, setOtp] = useState('');
    const [message, setMessage] = useState('');

    const handleRegisterChange = (e) => {
        setRegisterData({ ...registerData, [e.target.name]: e.target.value });
    };

    const handleRegister = async () => {
        const response = await registerUser(registerData);
        setMessage(response.message);
    };

    const handleSendOtp = async () => {
        const response = await sendOtp(mobileNumber);
        setMessage(response.message);
    };

    const handleVerifyOtp = async () => {
        const response = await verifyOtp(mobileNumber, otp);
        setMessage(response.message);
    };

    return (
        <div className="App">
            <h1>OTP Authentication</h1>

            {/* Registration Form */}
            <div className="form-container">
                <h2>Register</h2>
                <input type="text" name="name" placeholder="Name" value={registerData.name} onChange={handleRegisterChange} />
                <input type="number" name="age" placeholder="Age" value={registerData.age} onChange={handleRegisterChange} />
                <input type="text" name="gender" placeholder="Gender" value={registerData.gender} onChange={handleRegisterChange} />
                <input type="tel" name="mobileNumber" placeholder="Mobile Number" value={registerData.mobileNumber} onChange={handleRegisterChange} />
                <button onClick={handleRegister}>Register</button>
            </div>

            {/* Login Form */}
            <div className="form-container">
                <h2>Send OTP</h2>
                <input type="tel" placeholder="Mobile Number" value={mobileNumber} onChange={(e) => setMobileNumber(e.target.value)} />
                <button onClick={handleSendOtp}>Send OTP</button>
            </div>

            {/* OTP Verification Form */}
            <div className="form-container">
                <h2>Verify OTP</h2>
                <input type="number" placeholder="Enter OTP" value={otp} onChange={(e) => setOtp(e.target.value)} />
                <button onClick={handleVerifyOtp}>Verify OTP</button>
            </div>

            {/* Message Display */}
            {message && <p className="message">{message}</p>}
        </div>
    );
}

export default App;
