import React, { useState, useEffect, useRef } from "react";

const Chatbot = () => {
    const [userInput, setUserInput] = useState("");
    const [responses, setResponses] = useState([]);
    const [showQR, setShowQR] = useState(false); // To control the display of QR code
    const chatboxRef = useRef();

    useEffect(() => {
        const welcomeMessage = "ðŸ‘‹ Welcome to the Health Chatbot! How can I assist you today?";
        setResponses([welcomeMessage]);

        const inputField = document.getElementById("user-input");
        if (inputField) {
            inputField.focus();
        }
    }, []);

    const handleUserInput = (e) => {
        setUserInput(e.target.value);
    };

    const handleSend = async () => {
        if (userInput.trim()) {
            setResponses((prevResponses) => [...prevResponses, `You said: ${userInput}`]);

            try {
                const response = await fetch("http://127.0.0.1:5000/suggest_doctor", {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify({ symptoms: userInput }),
                });

                if (!response.ok) {
                    throw new Error(`Server error: ${response.statusText}`);
                }

                const data = await response.json();
                setResponses((prevResponses) => [
                    ...prevResponses,
                    data.message || "No response from server.",
                ]);
            } catch (error) {
                console.error("Error:", error);
                setResponses((prevResponses) => [
                    ...prevResponses,
                    `Error: ${error.message || "Unable to reach the backend."}`,
                ]);
            }

            setUserInput(""); // Clear input after sending
        }
    };

    const handleClearChat = () => {
        setResponses([]);
        const welcomeMessage = "ðŸ‘‹ Welcome to the Health Chatbot! How can I assist you today?";
        setResponses([welcomeMessage]);
    };

    const handlePayment = async () => {
        const options = {
            key: "rzp_test_lmkOFuIPmT2vi9	", // Replace with your Razorpay key
            amount: 1000 * 100, // Amount in paise (50000 paise = â‚¹500)
            currency: "INR",
            name: "Health Chatbot",
            description: "Payment for consultation",
            image: "https://your-logo-url.com/logo.png", // Replace with your logo URL
            handler: function (response) {
                alert(`Payment successful! Payment ID: ${response.razorpay_payment_id}`);
            },
            prefill: {
                name: "CareLink", // Replace with the actual user's name
                email: "carelink@gmail.com", // Replace with the actual user's email
                contact: "9999999999", // Replace with the actual user's contact
            },
            theme: {
                color: "#F37254",
            },
        };

        const razorpay = new window.Razorpay(options);
        razorpay.open();
    };

    useEffect(() => {
        if (chatboxRef.current) {
            chatboxRef.current.scrollTop = chatboxRef.current.scrollHeight;
        }
    }, [responses]);

    const handleShowQR = () => {
        setShowQR(true); // Display the QR code for payment
    };

    return (
        <div className="min-h-screen flex items-center justify-center relative overflow-hidden">
            <div
                className="absolute inset-0 bg-cover bg-center"
                style={{
                    backgroundImage:
                        "url('https://img.freepik.com/free-vector/people-waiting-hospital-reception-while-wearing-medical-masks_23-2148806775.jpg?w=1060&t=st=1728811919~exp=1728812519~hmac=b79e4edd66a7da5c7153bc6b7fc572954a425ce0020f7d178139ec54d61c4877')",
                }}
            >
                <div className="absolute inset-0 bg-black opacity-50 backdrop-blur-lg"></div>
            </div>
            <div className="chatbot-container w-full max-w-md p-6 bg-white rounded-lg shadow-2xl border border-gray-200 mt-10 z-10 relative transform transition-transform duration-300 hover:scale-105">
                <h2 className="text-2xl font-semibold text-center mb-4 text-gray-800">
                    ðŸ¤– Health Chatbot
                </h2>
                <div
                    className="chatbox border border-gray-300 p-4 h-72 overflow-y-auto rounded-lg bg-gray-100 shadow-inner"
                    ref={chatboxRef}
                >
                    {responses.map((response, index) => (
                        <p
                            key={index}
                            className="mb-2 text-gray-800 transition-all duration-300 transform hover:text-blue-600"
                        >
                            {response}
                        </p>
                    ))}
                </div>
                <div className="flex flex-col md:flex-row mt-4 space-y-2 md:space-y-0 md:space-x-2">
                    <input
                        id="user-input"
                        type="text"
                        value={userInput}
                        onChange={handleUserInput}
                        placeholder="Type your symptoms..."
                        className="border border-gray-300 rounded-lg p-2 flex-1 focus:outline-none focus:ring-2 focus:ring-blue-400 transition duration-300 shadow-md hover:shadow-lg"
                    />
                    <button
                        onClick={handleSend}
                        className="bg-blue-600 text-white rounded-lg p-2 hover:bg-blue-700 transition duration-300 shadow-md transform hover:scale-105"
                    >
                        Send
                    </button>
                </div>
                <button
                    onClick={handleClearChat}
                    className="bg-red-600 text-white rounded-lg p-2 mt-2 w-full hover:bg-red-700 transition duration-300 shadow-md transform hover:scale-105"
                >
                    Clear Chat
                </button>
                <button
                    onClick={handlePayment}
                    className="bg-green-600 text-white rounded-lg p-2 mt-2 w-full hover:bg-green-700 transition duration-300 shadow-md transform hover:scale-105"
                >
                    Pay via Razorpay
                </button>
                <button
                    onClick={handleShowQR}
                    className="bg-purple-600 text-white rounded-lg p-2 mt-2 w-full hover:bg-purple-700 transition duration-300 shadow-md transform hover:scale-105"
                >
                    Show UPI QR Code
                </button>

                {/* QR Code Display Section */}
                {showQR && (
                    <div className="qr-code mt-4 text-center">
                        <p>Scan to Pay via UPI:</p>
                        <img
                            src="https://your-qr-code-image-url.com/upi_qr_code.png" // Replace with your actual QR code image URL
                            alt="UPI QR Code"
                            className="w-48 h-48 mx-auto mt-2"
                        />
                        <p>UPI ID: your-upi-id@bank</p> {/* Update with your actual UPI ID */}
