import React, { useState } from 'react';

const LabTest = () => {
    const [selectedTest, setSelectedTest] = useState(null);

    const labTests = [
        { name: 'Complete Blood Count (CBC)', price: '₹500' },
        { name: 'Lipid Profile', price: '₹700' },
        { name: 'Liver Function Test (LFT)', price: '₹900' },
        { name: 'Kidney Function Test (KFT)', price: '₹800' },
        { name: 'Thyroid Profile', price: '₹600' },
    ];

    const handleSelectTest = (event) => {
        const selectedName = event.target.value;
        const test = labTests.find((test) => test.name === selectedName);
        setSelectedTest(test);
    };

    return (
        <div className="container mx-auto mt-10 p-4 bg-white shadow-lg rounded-md">
            <h1 className="text-3xl font-bold text-center mb-6">Lab Test Information</h1>
            <div className="text-center">
                <select
                    onChange={handleSelectTest}
                    className="block w-2/3 md:w-1/2 mx-auto p-2 border border-gray-300 rounded-md text-lg focus:outline-none"
                    defaultValue=""
                >
                    <option value="" disabled>Select a Lab Test</option>
                    {labTests.map((test, index) => (
                        <option key={index} value={test.name}>{test.name}</option>
                    ))}
                </select>

                {selectedTest && (
                    <div className="mt-6 text-lg">
                        <p><strong>Test Name:</strong> {selectedTest.name}</p>
                        <p><strong>Price:</strong> {selectedTest.price}</p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default LabTest;
