import React, { useState } from 'react';

const labTests = [
    { name: "General Physician (Dr. Arjun Sharma)", price: "₹740", guidelines: "Avoid eating heavy meals at least 2 hours before the appointment." },
    { name: "Neurologist (Dr. Suresh Rao)", price: "₹10,660", guidelines: "Ensure a full night's sleep, avoid stimulants, and list down any observed symptoms." },
    // Add more entries as needed
];

function LabTest() {
    const [selectedTest, setSelectedTest] = useState(null);
    const [paidTests, setPaidTests] = useState([]);

    const handlePayment = (testName) => {
        // Simulate payment process
        alert(`Payment successful for ${testName}`);
        setPaidTests((prevPaidTests) => [...prevPaidTests, testName]);
    };

    const displayGuidelines = (test) => {
        if (paidTests.includes(test.name)) {
            setSelectedTest(test);
        } else {
            alert("Please make a payment to view the guidelines.");
        }
    };

    return (
        <div style={{ padding: '20px', fontFamily: 'Arial' }}>
            <h1>Lab Tests</h1>
            <ul style={{ listStyleType: 'none', padding: 0 }}>
                {labTests.map((test, index) => (
                    <li
                        key={index}
                        style={{
                            border: '1px solid #ccc',
                            padding: '10px',
                            marginBottom: '10px',
                            borderRadius: '5px',
                        }}
                    >
                        <h3>{test.name}</h3>
                        <p>Price: {test.price}</p>
                        {paidTests.includes(test.name) ? (
                            <button
                                style={{
                                    padding: '8px 12px',
                                    backgroundColor: '#4CAF50',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '5px',
                                    cursor: 'pointer',
                                }}
                                onClick={() => displayGuidelines(test)}
                            >
                                View Guidelines
                            </button>
                        ) : (
                            <button
                                style={{
                                    padding: '8px 12px',
                                    backgroundColor: '#f44336',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '5px',
                                    cursor: 'pointer',
                                }}
                                onClick={() => handlePayment(test.name)}
                            >
                                Make Payment
                            </button>
                        )}
                    </li>
                ))}
            </ul>
            {selectedTest && (
                <div
                    style={{
                        marginTop: '20px',
                        padding: '10px',
                        border: '1px solid #ccc',
                        borderRadius: '5px',
                        backgroundColor: '#f9f9f9',
                    }}
                >
                    <h2>Guidelines for {selectedTest.name}</h2>
                    <p>{selectedTest.guidelines}</p>
                </div>
            )}
        </div>
    );
}

export default LabTest;
