import React, { useState } from 'react';
import axios from 'axios';

const Chatbot = () => {
    const [userInput, setUserInput] = useState('');
    const [messages, setMessages] = useState([]);
    const [paymentStatus, setPaymentStatus] = useState(false);
    const [paymentAmount, setPaymentAmount] = useState('');

    // Handle user input submission
    const handleSend = async () => {
        if (userInput.trim() === '') return;

        const newMessage = { text: userInput, sender: 'user' };
        setMessages((prevMessages) => [...prevMessages, newMessage]);
        
        // Make API call to suggest doctors
        try {
            const response = await axios.post('http://localhost:5000/suggest_doctor', {
                symptoms: userInput,
                payment_status: paymentStatus,
            });

            const botMessage = { text: response.data.message, sender: 'bot' };
            setMessages((prevMessages) => [...prevMessages, botMessage]);
            setUserInput(''); // Clear input after sending
        } catch (error) {
            console.error('Error sending message:', error);
            const errorMessage = { text: 'âš ï¸ An error occurred while getting doctor suggestions.', sender: 'bot' };
            setMessages((prevMessages) => [...prevMessages, errorMessage]);
        }
    };

    // Handle payment amount input change
    const handlePaymentChange = (e) => {
        setPaymentAmount(e.target.value);
    };

    // Handle payment confirmation
    const handlePayment = () => {
        if (parseFloat(paymentAmount) > 0) {
            setPaymentStatus(true);
            alert('Payment successful! You can now ask for doctor suggestions.');
            setPaymentAmount('');
        } else {
            alert('Please enter a valid payment amount.');
        }
    };

    return (
        <div className="chatbot-container">
            <div className="chat-window">
                {messages.map((msg, index) => (
                    <div key={index} className={`message ${msg.sender}`}>
                        {msg.text}
                    </div>
                ))}
            </div>
            <div className="input-area">
                <input
                    type="text"
                    value={userInput}
                    onChange={(e) => setUserInput(e.target.value)}
                    placeholder="Describe your symptoms..."
                    className="input-field"
                />
                <button onClick={handleSend} className="send-button">Send</button>
            </div>
            <div className="payment-area mt-4">
                <input
                    type="number"
                    value={paymentAmount}
                    onChange={handlePaymentChange}
                    placeholder="Enter payment amount"
                    className="border border-gray-300 rounded-lg p-2 w-full"
                />
                <button
                    onClick={handlePayment}
                    className="bg-green-600 text-white rounded-lg p-2 mt-2 w-full"
                >
                    Pay Now
                </button>
            </div>
        </div>
    );
};

export default Chatbot;
