import React, { useState } from 'react';

const LabTest = () => {
    const [selectedTest, setSelectedTest] = useState(null);

    const labTests = [
        { name: 'Complete Blood Count (CBC)', price: '₹500' },
        { name: 'Lipid Profile', price: '₹700' },
        { name: 'Liver Function Test (LFT)', price: '₹900' },
        { name: 'Kidney Function Test (KFT)', price: '₹800' },
        { name: 'Thyroid Profile', price: '₹600' },
    ];

    const handleSelectTest = (event) => {
        const selectedName = event.target.value;
        const test = labTests.find((test) => test.name === selectedName);
        setSelectedTest(test);
    };

    return (
        <div className="container mx-auto mt-10 p-6 bg-gradient-to-r from-blue-50 to-blue-100 shadow-xl rounded-xl">
            <h1 className="text-4xl font-extrabold text-center mb-8 text-blue-600">Lab Test Information</h1>
            <div className="text-center flex flex-col items-center space-y-6">
                <select
                    onChange={handleSelectTest}
                    className="block w-3/4 md:w-1/2 lg:w-1/3 p-3 border border-gray-300 rounded-lg text-lg focus:outline-none focus:border-blue-500 transition duration-200 shadow-md hover:bg-blue-50"
                    defaultValue=""
                >
                    <option value="" disabled>Select a Lab Test</option>
                    {labTests.map((test, index) => (
                        <option key={index} value={test.name}>{test.name}</option>
                    ))}
                </select>

                {selectedTest && (
                    <div className="mt-4 p-4 bg-white shadow-lg rounded-lg w-3/4 md:w-1/2 lg:w-1/3 border border-blue-200 text-left transform transition-all duration-200 hover:scale-105">
                        <p className="text-xl font-semibold text-blue-700"><strong>Test Name:</strong> {selectedTest.name}</p>
                        <p className="text-xl font-semibold text-blue-700"><strong>Price:</strong> {selectedTest.price}</p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default LabTest;
